ckbox') ) :	/**	 * Sanitization callback for checkboxes.	 *	 * @param string $value Color scheme name value.	 * @return string Color scheme name.	 */function valeo_sanitize_checkbox( $value ) {	if ( $value == 1 ) {		return 1;	} else {		return '';	}}endif; // valeo_sanitize_checkboxif ( ! function_exists('valeo_sanitize_text') ) :    /**     * Sanitization callback for text.     *     * @param string $value text.     * @return string with allowed html tags only.     */function valeo_sanitize_text( $value ) {    return wp_kses_post( $value );}endif; // valeo_sanitize_text.if ( ! function_exists('valeo_sanitize_counter') ) :	/**	 * Sanitization callback for counters.	 *	 * @param string $value html.	 * @return string with html tags.	 */	function valeo_sanitize_counter( $value ) {		return $value;	}endif; // valeo_sanitize_counter.if ( ! function_exists('valeo_sanitize_single_post_video') ) :	/**	 * Sanitization callback for single post video position.	 *	 * @param string $value sidebar position.	 * @return string with sidebar position.	 */	function valeo_sanitize_single_post_video( $value ) {		$valid = array(			'default' => 'Default',			'wide' => 'Wide',		);		if ( array_key_exists( $value, $valid ) ) {			return $value;		} else {			return 'default';		}	}endif; // valeo_sanitize_single_post_video.if ( ! function_exists('valeo_sanitize_sidebar') ) :    /**     * Sanitization callback for sidebar position.     *     * @param string $value sidebar position.     * @return string with sidebar position.     */function valeo_sanitize_sidebar( $value ) {    $valid = array(        'left' => 'Left',        'right' => 'Right',        'none' => 'None',    );    if ( array_key_exists( $value, $valid ) ) {        return $value;    } else {        return 'right';    }}endif; // valeo_sanitize_sidebar.if ( ! function_exists('valeo_sanitize_main_slider') ) :	/**	 * Sanitization callback for main sli