nd				'#3d6889', // accent_color				'#999999', // meta_textcolor				'#999999', // content_textcolor                '#292a2e', // accent_color_2			),		),		'dark'    => array(			'label'  => esc_html__( 'Dark', 'valeo' ),			'colors' => array(				'#ffffff', // header_textcolor				'#000000', // background_color				'#404240', // sidebar_background				'#aaaaaa', // menu_background				'#2d00f7', // accent_color				'#999999', // meta_textcolor				'#999999', // content_textcolor                '#2a6616', // accent_color_2			),		),	) );}endif;if ( ! function_exists('valeo_get_color_scheme') ) :/** * Get the current Valeo color scheme. * * @return array An associative array of either the current or default color scheme hex values. */function valeo_get_color_scheme() {	$color_scheme_option = get_theme_mod( 'color_scheme', 'default' );	$color_schemes       = valeo_get_color_schemes();	if ( array_key_exists( $color_scheme_option, $color_schemes ) ) {		return $color_schemes[ $color_scheme_option ]['colors'];	}	return $color_schemes['default']['colors'];}endif; // valeo_get_color_schemeif ( ! function_exists('valeo_get_color_scheme_choices') ) :/** * Returns an array of color scheme choices registered for Valeo. * * @return array Array of color schemes. */function valeo_get_color_scheme_choices() {	$color_schemes                = valeo_get_color_schemes();	$color_scheme_control_options = array();	foreach ( $color_schemes as $color_scheme => $value ) {		$color_scheme_control_options[ $color_scheme ] = $value['label'];	}	return $color_scheme_control_options;}endif; // valeo_get_color_scheme_choicesif ( ! function_exists('valeo_sanitize_color_scheme') ) :/** * Sanitization callback for color schemes. * * @param string $value Color scheme name value. * @return string Color scheme name. */function valeo_sanitize_color_scheme( $value ) {	$color_schemes = valeo_get_color_scheme_choices();	if ( ! array_key_exists( $value,