r }}',		'menu_background__hover'	=> '{{ data.menu_background__hover}}',	);	?>	<script type="text/html" id="tmpl-valeo-color-scheme">		<?php echo valeo_get_color_scheme_css( $colors );?>	</script>	<?php}endif;add_action( 'customize_controls_print_footer_scripts', 'valeo_color_scheme_css_template' );if ( ! function_exists('valeo_sidebar_class') ) :	/**	 * CSS Class for blog pages content positioning.	 *	 * @param string $doc_part which container.	 * @return string $value container class.	 */	function valeo_sidebar_class( $doc_part, $sidebar_var = 'blog_page_sidebar' ) {		global $post;		$blog_page_sidebar = "";		if(!empty($_GET["sidebar_position"])){			$query_value = esc_attr($_GET["sidebar_position"]);		}		if(isset($query_value) && in_array($query_value, array("left","right","none"))) {			$blog_page_sidebar = $query_value;		} elseif( is_home() && valeo_is_frontpage() ) {			switch( valeo_get_customizer_option( 'front_posts_layout' ) ) {				case "1colwsl":				case "2colwsl":				case "nonewsl": $blog_page_sidebar = "left"; break;				case "1colws":				case "2colws":				case "nonews": $blog_page_sidebar = "right"; break;				case "1col":				case "2col":				case "none": $blog_page_sidebar = "none"; break;				default: break;			}		} else {			$blog_page_sidebar = valeo_get_customizer_option( $sidebar_var );            // WooCommerce Single product            if ( is_singular ( 'product' ) ) {                $blog_page_sidebar = "none";            }		}		if ( function_exists( 'fw_get_db_post_option' ) && is_singular() ) {			$query_value = fw_get_db_post_option( $post->ID, 'sidebar_position' );			if ( isset( $query_value ) && in_array( $query_value, array( "left", "right", "none" ) ) ) {				$blog_page_sidebar = $query_value;			}		}		$value = "";		if( $doc_part == "container" ) {			switch( $blog_page_sidebar ) {				case  "left": $value = "sleft"; break;				case "right": $value = "sright"; break;				case  "none": $value = "snone"; break;				default: break;			}		}		if( $doc_part == "content" ) {			switch( $blog_page_sidebar ) {				case  "left": $value = "site__content col-md-8 col-md-push-4 col-xs-12"; break;				case "right": $value = "site__content col-md-8 col-xs-12"; break;				case  "none": $value = "site__content col-xs-12"; break;				default: break;			}		}		if( $doc_part == "sidebar" ) {			switch( $blog_page_sidebar ) {				case  "left": $value = "site__sidebar widget-width__side col-md-4 col-md-pull-8 col-xs-12 sidebar-visible"; break;				case "right": $value = "site__sidebar widget-width__side col-md-4 col-xs-12 sidebar-visible sidebar-right"; break;				default: break;			}		}		return esc_attr( $value );	}endif; // valeo_sidebar_classif ( ! function_exists('valeo_sidebar_visible') ) :    /**     * Check if sidebar should be displayed.     *     * @return bool $value.     */    function valeo_sidebar_visible() {	    global $post;	    $blog_page_sidebar = valeo_get_customizer_option( 'blog_page_sidebar' );	    switch( valeo_get_customizer_option( 'front_posts_layout' ) ) {		    case "1colwsl":		    case "2colwsl":		    case "nonewsl": $blog_page_sidebar = "left"; break;		    case  "1colws":		    case  "2colws":		    case  "nonews": $blog_page_sidebar = "right"; break;		    case    "1col":		    case    "2col":		    case    "none": $blog_page_sidebar = "none"; break;		    default: break;	    }	    $value = true;	    if ( function_exists('fw_get_db_post_option') && is_singular() ) {		    $query_value = fw_get_db_post_option($post->ID, 'sidebar_position');		    if(isset($query_value) && in_array($query_value, array("left","right","none"))) {			    $blog_page_sidebar = $query_value;		    }	    }        if( $blog_page_sidebar == "none" ) {            $value = false;        }        return $value;    }endif; // valeo_sidebar_visibleif ( ! function_exists('valeo_is_blog_view_excerpt') ) :function valeo_is_blog_view_excerpt() {    $theme_options = valeo_get_theme_mods();    if( !valeo_is_unyson_page_builder_for_post_type() ) {        if ( !empty ( $GLOBALS['wp_customize'] ) ) {            global $wp_customize;            $post_values = $wp_customize->unsanitized_post_values();            if( !empty($post_values['blog_post_visibility']) ) {                if ($post_values['blog_post_visibility'] == 'excerpt') {                    return true;                } else {                    return false;                }            } elseif( !empty($theme_options['blog_post_visibility']) && $theme_options['blog_post_visibility'] == 'excerpt' ) {                return true;            }        } elseif( !empty($theme_options['blog_post_visibility']) && $theme_options['blog_post_visibility'] == 'excerpt' ) {            return true;        }    }    return false;}endif;if ( ! function_exists( 'valeo_header_counter' ) ) :	function valeo_header_counter() {		echo valeo_get_customizer_option( 'header_counter' );	}endif; // valeo_header_counteradd_action( 'wp_head', 'valeo_header_counter', 9999 );if ( ! function_exists( 'valeo_footer_counter' ) ) :	function valeo_footer_counter() {		if (valeo_get_customizer_option('footer_counter')) {			echo '<span class="footer_counter" >'. valeo_get_customizer_option('footer_counter') . '</span>';		}	}endif; // valeo_footer_counteradd_action( 'wp_footer', 'valeo_footer_counter', 9999 );if ( ! function_exists( 'valeo_get_customizer_option' ) ) :	function valeo_get_customizer_option( $option_name ) {		$theme_options = valeo_get_theme_mods();		$option_value  = $theme_options[ $option_name ];		if ( ! empty ( $GLOBALS['wp_customize'] ) ) {			global $wp_customize;            // array of changed options in Customizer			$post_values = $wp_customize->unsanitized_post_values();			if ( array_key_exists( $option_name, $post_values) ) {                $option_value = $post_values[ $option_name ];            }		}		return $option_value;	}endif; // valeo_get_customizer_option/** *  When we in Customizer Mode - returns array of changed options * * @return array|bool */if ( ! function_exists( 'valeo_get_customizer_post_options' ) ) :function valeo_get_customizer_post_options() {    $post_values = false;    if ( ! empty ( $GLOBALS['wp_customize'] ) ) {        global $wp_customize;        // array of changed options in Customizer        $post_values = $wp_customize->unsanitized_post_values();        $post_values = count($post_values) > 0 ? $post_values : false;    }    return $post_values;}endif; // valeo_get_customizer_post_optionsif ( ! function_exists('valeo_is_active_widget') ) :	function valeo_is_active_widget( $widget_name ) {		if(!$widget_name){			return false;		}		if ( is_active_widget( false, false, $widget_name ) ){			return true;		}		elseif(!empty($_POST["customized"]) && preg_match( "/widget_".$widget_name."\[/", $_POST["customized"])){			return true;		}		return false;	}endif; // valeo_is_active_widgetif ( ! function_exists('valeo_is_unyson_page_builder_for_post_type') ) :    function valeo_is_unyson_page_builder_for_post_type( $post_type = 'post' ) {        $use_posts_builder = false;        if( defined('FW') && fw_ext('page-builder') ) {            $builder_options = fw_ext('page-builder')->get_db_settings_option();            if( isset( $builder_options['post_types'][$post_type] ) ) {                $use_posts_builder = $builder_options['post_types'][$post_type];            }        }        return $use_posts_builder;    }endif; // valeo_is_unyson_page_builder_for_post_typeif ( ! function_exists('valeo_get_unyson_sliders') ) {	function valeo_get_unyson_sliders() {		$unyson_sliders = array( -1 => 'No slider' );		if ( defined( "FW" ) && fw_ext('slider') ) {			$unyson_sliders = fw()->extensions->get( 'slider' )->get_populated_sliders_choices();			if ( empty( $unyson_sliders ) ) {				$unyson_sliders = array( -1 => 'No slider' );			} else {				$unyson_sliders = array( -1 => 'No slider' ) + $unyson_sliders;			}		}		return $unyson_sliders;	}}